Agent.Script:
#!/bin/bash ############ {0} ############ TITLE=$(echo -e "$TITLE") MESSAGE=$(echo -e "$MESSAGE")
case "$IMPORTANCE" in 'normal' ) PRIORITY="0" ;; 'warning' ) PRIORITY="1" ;; 'alert' ) PRIORITY="2" ;; esac
curl -s -k \ -F "apikey=$API_KEY" \ -F "application=$APP_NAME" \ -F "event=$TITLE" \ -F "description=$MESSAGE" \ -F "priority=$PRIORITY" \ https://api.prowlapp.com/publicapi/add 2>&1

#!/bin/bash ########## {0} ########## TITLE=$(echo -e "$TITLE") MESSAGE=$(echo -e "$MESSAGE")
case "$IMPORTANCE" in 'normal' ) PRIORITY="0" ;; 'warning' ) PRIORITY="1" ;; 'alert' ) PRIORITY="2" ;; esac
curl -s -k \ -d "apikey=$API_KEY" \ -d "application=$APP_NAME" \ -d "event=$TITLE" \ -d "description=$MESSAGE" \ -d "priority=$PRIORITY" \ https://www.notifymyandroid.com/publicapi/notify 2>&1
